frontApp.controller("categoryPostController",["$scope","$http","$window","CategoryPost",function(n,t,i,r){n.categoryPosts=[];n.posts=[];n.idCategory=angular.element("#idCategory").val();n.baiVietNews=[];n.baiVietTops=[];n.khachHangDoiTacs=[];n.congTrinhTieuBieus=[];t.get("/API/CategoriesAPI/").success(function(t){var i=r.getallCategory(t);angular.forEach(i,function(t){t.idCategoryParent=="1"&&n.categoryPosts.push(t)})});t.get("/API/PostsAPI/").success(function(t){angular.forEach(t,function(t){t.idCategory==n.idCategory&&n.posts.push(t);t.featured==1&&t.idCategory==2&&n.baiVietTops.push(t);t.idCategory==2&&n.baiVietNews.push(t);t.idCategory==6&&n.khachHangDoiTacs.push(t);t.idCategory==5&&n.congTrinhTieuBieus.push(t)})})}]);