frontApp.controller("categoryProductController",["$scope","$http","$window","CategoryProduct",function(n,t,i,r){n.categoryProducts=[];n.products=[];n.idCategoryProduct=angular.element("#idCategoryProduct").val();n.product={};n.id;n.images=[];n.image="/Content/Images/images/BoSuuTap/bosuutap-2.png";n.spNoiBats=[];n.gachMens=[];n.thietBiVeSinhs=[];n.thietBiNhaTams=[];n.tranh3Ds=[];t.get("/API/CategoryProductsAPI/").success(function(t){var i=r.getallCategoryProduct(t);angular.forEach(i,function(t){t.idCategoryProductParent=="1"&&n.categoryProducts.push(t)})});n.show=function(i){n.id=i;t.get("/API/ProductsAPI/"+i).success(function(t){n.product=t})};t.get("/API/ProductsAPI/").success(function(t){angular.forEach(t,function(t){t.idCategoryProduct==n.idCategoryProduct&&n.products.push(t)});angular.forEach(t,function(t){t.feature=="1"&&n.spNoiBats.push(t)});angular.forEach(t,function(t){t.idCategoryProduct==2&&n.gachMens.push(t)});angular.forEach(t,function(t){t.idCategoryProduct==3&&n.thietBiVeSinhs.push(t)});angular.forEach(t,function(t){t.idCategoryProduct==4&&n.thietBiNhaTams.push(t)});angular.forEach(t,function(t){t.idCategoryProduct==5&&n.tranh3Ds.push(t)})});t.get("/API/imageAPI/").success(function(t){angular.forEach(t,function(t){t.idCategoryProduct==n.idCategoryProduct&&n.images.push(t)})});n.load=function(t){n.image=t}}]);